function readFileFromUrl(url) {
  return new Promise((resolve, reject) => {
    request({ uri: url }, (error, response, body) => {
      if (error) {
        //console.log("ERROR");
        console.error(error);
        return reject(error);
      }
      return resolve(body);
    });
  });
}

