function generateLoader(projectId, staticUrl, imageObj) {
  return `"use strict";
            (function () {
                ${config.loaderPreRequisite}
                ${generateLoaderFunctions(projectId, staticUrl)}
                ${generateLoaderSettings(imageObj)}
                window.mainpage =   '${generateMainHTML()}';
                window.leftpanel =  '${generateLeftPanelHTML(imageObj)}';   
                window.rightpanel = '${generateRightPanelHTML()}';
                window.templateOpenView = '${buildTemplateHTMLView(imageObj)}';
                window.loadPermissionView = '${buildTemplatePermissionView(
                  imageObj
                )}';
    
            })();
            `;
}

function generateLoaderSettings(imageObj) {
  return `window.jubiModal={
                static:{
                    images:${JSON.stringify(imageObj)}
                }
            };`;
}

